import { Component } from "@angular/core";
import { NgModel } from "@angular/forms";

interface EducationDetail {
    schoolName: string;
    startYear: string;
    endYear: string;
  }
  
@Component({
    selector: 'app-profile',
    templateUrl: './profile.component.html', 
    
})
export class ProfileComponent{
    userdata = {
        name: '',
        age: '',
        email: '',
        address: '',
        educationDetails: [ ] as EducationDetail[],
        hobbies: [''],
        projects: [''],
        schoolName:'',
        startYear:'',
        endYear:'',

      };
      
      addEducation() {
       this.userdata.educationDetails.push({ schoolName: '', startYear: '', endYear: '' });
      }
    
      removeEducation(index: number) {
        this.userdata.educationDetails.splice(index, 1);
      }
    
      submitForm() {
        console.log('Form submitted:', this.userdata);
        // Add further logic to save or process the data
      }

}
