  selector: 'app-home',
  templateUrl: './main.component.html',
})

export class MainComponent{
    profile = new FormGroup({
    name : new FormControl(''),
    gender:new FormControl(''),
    age: new FormControl(''),
    state: new FormControl(''),
    skills:new FormControl('')
